export const useViewKey = ({ enabled }: { enabled: boolean } = { enabled: true }) => {
    const { adapter, connected } = useWallet()
    const [viewKey, setViewKey] = useState<string | null>(null)
    const [loading, setLoading] = useState<boolean>(enabled)
    const [error, setError] = useState<null | Error>(null)

    const requestViewKey = useCallback(async () => {
        setLoading(true)
        setError(null)

        try {
            if (!connected) {
                throw new WalletNotConnectedError()
            }

            if (!adapter) {
                throw new WalletNotSelectedError()
            }

            if (adapter && 'requestViewKey' in adapter) {
                const viewKey = await (adapter as any).requestViewKey()

                setViewKey(viewKey)
            } else {
                throw new WalletError('Not implemented in your wallet provider')
            }
        } catch (error: any) {
            setError(error)
        } finally {
            setLoading(false)
        }
    }, [connected, adapter])

    useEffect(() => {
        if (enabled) {
            requestViewKey()
        }
    }, [requestViewKey])

    return useMemo(
        () => ({ viewKey, loading, error, requestViewKey }),
        [viewKey, loading, error, requestViewKey],
    )
}
import {
    AleoTransaction,
    WalletError,
    WalletNotConnectedError,
} from '@demox-labs/aleo-wallet-adapter-base'
import { useCallback, useMemo, useState } from 'react'
import { useWallet } from './useWallet'

export const useBulkTransactions = () => {
    const { connected, adapter } = useWallet()
    const [error, setError] = useState<null | Error>(null)
    const [loading, setLoading] = useState(false)
    const [transactionIds, setTransactionIds] = useState<null | string[]>(null)

    const executeBulkTransactions = useCallback(
        async (aleoTransactions: AleoTransaction[]) => {
            try {
                setLoading(true)
                if (!connected) throw new WalletNotConnectedError()

                if (adapter && 'requestBulkTransactions' in adapter) {
                    const transactionIds = await adapter.requestBulkTransactions(aleoTransactions)

                    setTransactionIds(transactionIds)

                    return transactionIds
                } else {
                    throw new WalletError('Not implemented in your wallet provider')
                }
            } catch (err: any) {
                setError(err)

                return null
            } finally {
                setLoading(false)
            }
        },
        [adapter],
    )

    return useMemo(
        () => ({ executeBulkTransactions, error, transactionIds, loading }),
        [executeBulkTransactions, error, transactionIds, loading],
    )
}
import {
    AleoTransaction,
    WalletError,
    WalletNotConnectedError,
} from '@demox-labs/aleo-wallet-adapter-base'
import { useCallback, useMemo, useState } from 'react'
import { useWallet } from './useWallet'

export const useTransaction = () => {
    const { connected, adapter } = useWallet()
    const [error, setError] = useState<null | Error>(null)
    const [loading, setLoading] = useState(false)
    const [transactionId, setTransactionId] = useState<null | string>(null)

    const executeTransaction = useCallback(async (aleoTransaction: AleoTransaction) => {
        try {
            setLoading(true)
            if (!connected) throw new WalletNotConnectedError()

            if (adapter && 'requestTransaction' in adapter) {
                const transactionId = await adapter.requestTransaction(aleoTransaction)

                setTransactionId(transactionId)

                return transactionId
            } else {
                throw new WalletError('Not implemented in your wallet provider')
            }
        } catch (err: any) {
            setError(err)

            return null
        } finally {
            setLoading(false)
        }
    }, [])

    return useMemo(
        () => ({ executeTransaction, error, transactionId, loading }),
        [executeTransaction, error, transactionId, loading],
    )
}
import { WalletError, WalletNotConnectedError } from '@demox-labs/aleo-wallet-adapter-base'
import { useCallback, useMemo, useState } from 'react'
import { useWallet } from './useWallet'

export const useSignMessage = () => {
    const { connected, adapter } = useWallet()
    const [error, setError] = useState<null | Error>(null)
    const [loading, setLoading] = useState(false)
    const [data, setData] = useState<null | Uint8Array>(null)

    const signMessage = useCallback(
        async (message: Uint8Array) => {
            try {
                setLoading(true)
                if (!connected) throw new WalletNotConnectedError()

                if (adapter && 'signMessage' in adapter) {
                    const data = await adapter.signMessage(message)

                    setData(data)

                    return data
                } else {
                    throw new WalletError('Not implemented in your wallet provider')
                }
            } catch (err: any) {
                setError(err)

                return null
            } finally {
                setLoading(false)
            }
        },
        [connected, adapter],
    )

    return useMemo(
        () => ({ signMessage, error, data, loading }),
        [signMessage, error, data, loading],
    )
}
